import React, { useState } from 'react';
import './assets/css/CompanyForm.css'; // External CSS for additional styling
import guestAvatar from './assets/image.png'; // Path to guest image

const CompanyForm = () => {
    const [formData, setFormData] = useState({
        companyName: '',
        registrationType: '',
        regdCity: '',
        regdArea: '',
        regdPin: '',
        regdExtraField: '',
        workCity: '',
        workArea: '',
        workPin: '',
        workExtraField: '',
        directorName: '',
        gender: '',
        email: '',
        phone: '',
        loginEmail: '',
        password: '',
        confirmPassword: '',
        profileImage: '',
        gstNumber: '',
        gstCertificate: '',
        fssaiNumber: '',
        fssaiCertificate: '',
        panNumber: '',
        panCard: '',
        companyServices: '',
        agreeToTerms: false,
    });

    const [errors, setErrors] = useState<{ [key: string]: string }>({});
    const [currentStep, setCurrentStep] = useState(1); // State to track the current step

    const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement | HTMLTextAreaElement>) => {
        const { name, value, type, checked } = e.target;
        setFormData({
            ...formData,
            [name]: type === 'checkbox' ? checked : value,
        });
    };

    const handleSubmit = (e: React.FormEvent, step: number) => {
        e.preventDefault();
        if (validateForm(step)) {
            if (step < 3) {
                setCurrentStep(step + 1); // Move to the next form if not the last step
            } else {
                console.log('All forms submitted successfully:', formData);
            }
        }
    };

    const validateForm = (step: number) => {
        const newErrors: { [key: string]: string } = {};
        
        if (step === 1) { // Validation for Company & Registration form
            if (!formData.companyName) newErrors.companyName = 'Company name is required';
            if (!formData.registrationType) newErrors.registrationType = 'Registration type is required';
        }

        if (step === 2) { // Validation for Contact & Login Information form
            if (!formData.email) newErrors.email = 'Email is required';
            if (!/\S+@\S+\.\S+/.test(formData.email)) newErrors.email = 'Invalid email address';
            if (!formData.phone || !/^\d{10}$/.test(formData.phone)) newErrors.phone = 'Valid phone number is required';
            if (!formData.password) newErrors.password = 'Password is required';
            if (formData.password !== formData.confirmPassword) newErrors.confirmPassword = 'Passwords do not match';
        }

        if (step === 3) { // Validation for Document Uploads form
            if (!formData.gstCertificate) newErrors.gstCertificate = 'GST Certificate is required';
            if (!formData.panCard) newErrors.panCard = 'PAN Card is required';
        }

        setErrors(newErrors);
        return Object.keys(newErrors).length === 0;
    };

    return (
        <div className="company-form-container">
            <h2 className="form-title">Company Information</h2>

            {/* Profile Image Section */}
            <div className="profile-image-container">
                <img
                    src={formData.profileImage || guestAvatar}
                    alt="Profile Preview"
                    className="profile-image"
                />
                <input
                    type="file"
                    id="profileImage"
                    name="profileImage"
                    accept="image/*"
                    onChange={handleChange}
                    className="profile-image-input"
                />
            </div>

            {/* Forms Container */}
            <div className="company-forms-container">
                {/* Form 1: Company & Registration */}
                {currentStep === 1 && (
                    <form onSubmit={(e) => handleSubmit(e, 1)} className="company-form-section">
                        <h3>Company & Registration</h3>
                        <div className="form-group">
                            <label>Company Name</label>
                            <input
                                type="text"
                                name="companyName"
                                value={formData.companyName}
                                onChange={handleChange}
                                className="full-width"
                            />
                            {errors.companyName && <span className="error">{errors.companyName}</span>}
                        </div>
                        <div className="form-group">
                            <label>Registration Type</label>
                            <select
                                name="registrationType"
                                value={formData.registrationType}
                                onChange={handleChange}
                                className="full-width"
                            >
                                <option value="">Select</option>
                                <option value="ltd">Ltd</option>
                                <option value="pvt ltd">Pvt Ltd</option>
                                <option value="llp">LLP</option>
                                <option value="partnership">Partnership</option>
                                <option value="firm">Firm</option>
                                <option value="upc">UPC</option>
                                <option value="individual">Individual</option>
                            </select>
                            {errors.registrationType && <span className="error">{errors.registrationType}</span>}
                        </div>
                        {/* Registered Address Fields */}
                        <div className="address-section">
                            <label>Registered Address</label>
                            <div className="address-fields">
                                <input
                                    type="text"
                                    name="regdCity"
                                    placeholder="City"
                                    value={formData.regdCity}
                                    onChange={handleChange}
                                    className="full-width"
                                />
                                {/* Additional address fields... */}
                            </div>
                        </div>

                        {/* Submit button for Form 1 */}
                        <div className="submit-btn-container">
                            <button type="submit" className="submit-btn">Next</button>
                        </div>
                    </form>
                )}

                {/* Form 2: Contact & Login Information */}
                {currentStep === 2 && (
                    <form onSubmit={(e) => handleSubmit(e, 2)} className="company-form-section">
                        <h3>Contact & Login Information</h3>
                        <div className="form-group">
                            <label>Email ID</label>
                            <input
                                type="email"
                                name="email"
                                value={formData.email}
                                onChange={handleChange}
                                className="full-width"
                            />
                            {errors.email && <span className="error">{errors.email}</span>}
                        </div>
                        {/* Additional fields like Phone Number, Login Email, etc. */}

                        {/* Submit button for Form 2 */}
                        <div className="submit-btn-container">
                            <button type="submit" className="submit-btn">Next</button>
                        </div>
                    </form>
                )}

                {/* Form 3: Document Uploads */}
                {currentStep === 3 && (
                    <form onSubmit={(e) => handleSubmit(e, 3)} className="company-form-section">
                        <h3>Document Uploads</h3>
                        <div className="form-group">
                            <label>GST Certificate</label>
                            <input
                                type="file"
                                name="gstCertificate"
                                accept="application/pdf"
                                onChange={handleChange}
                                className="full-width"
                            />
                            {errors.gstCertificate && <span className="error">{errors.gstCertificate}</span>}
                        </div>
                        {/* Additional document fields... */}

                        {/* Submit button for Form 3 */}
                        <div className="submit-btn-container">
                            <button type="submit" className="submit-btn">Submit</button>
                        </div>
                    </form>
                )}
            </div>
        </div>
    );
};

export default CompanyForm;
