import React, { useState, useEffect } from 'react';
import './assets/css/HomePage.css';
import image1 from './assets/bp-image1.jpg';
import image2 from './assets/bp-image2.jpg';
import image3 from './assets/bp-image3.jpg';
import image4 from './assets/bp-image4.jpg';
import image5 from './assets/bp-image5.jpg';
import image6 from './assets/bp-image6.jpg';

const images = [image1, image2, image3, image4, image5, image6];

const CarouselComponent: React.FC = () => {
  const [currentIndex, setCurrentIndex] = useState(0);

  const goToPrevious = () => {
    const newIndex = currentIndex === 0 ? images.length - 1 : currentIndex - 1;
    setCurrentIndex(newIndex);
  };

  const goToNext = () => {
    const newIndex = currentIndex === images.length - 1 ? 0 : currentIndex + 1;
    setCurrentIndex(newIndex);
  };

  const goToSlide = (index: number) => {
    setCurrentIndex(index);
  };

  // Continuous sliding effect
  useEffect(() => {
    const interval = setInterval(() => {
      goToNext();
    }, 3000); // Adjust the speed here (3000ms = 3 seconds)

    return () => clearInterval(interval);
  }, [currentIndex]);

  return (
    <div className="carousel-container">
      <div className="carousel-track">
        {/* Position arrows at the top */}
        <button className="arrow left-arrow" onClick={goToPrevious}>
          &#9664;
        </button>
        
        {images.map((image, index) => (
          <img
            key={index}
            src={image}
            alt={`carousel ${index}`}
            className={`carousel-image ${index === currentIndex ? 'active' : ''}`}
          />
        ))}
        
        <button className="arrow right-arrow" onClick={goToNext}>
          &#9654;
        </button>
      </div>

      {/* Carousel Indicators */}
      <div className="carousel-indicators">
        {images.map((_, index) => (
          <button
            key={index}
            type="button"
            className={`indicator ${index === currentIndex ? 'active' : ''}`}
            onClick={() => goToSlide(index)}
            aria-current={index === currentIndex ? 'true' : undefined}
          />
        ))}
      </div>
    </div>
  );
};

export default CarouselComponent;
