import React, { useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { AppState } from '../reducer';
import { addPost, removePost, updatePost } from '../actions/blogActions';
import { BlogPost } from '../blogTypes';
import 'bootstrap/dist/css/bootstrap.min.css'; // Import Bootstrap CSS

const Blog: React.FC = () => {
  const posts = useSelector((state: AppState) => state.blog.posts);
  const dispatch = useDispatch();
  const [title, setTitle] = useState('');
  const [content, setContent] = useState('');

  const handleAddPost = () => {
    const newPost: BlogPost = {
      id: Math.random(),
      title,
      content,
    };
    dispatch(addPost(newPost));
    setTitle('');
    setContent('');
  };

  const handleUpdatePost = (id: number) => {
    const updatedPost: BlogPost = {
      id,
      title,
      content,
    };
    dispatch(updatePost(updatedPost));
  };

  return (
    <div className="container mt-5">
      <h1 className="text-center mb-4">Blog</h1>
      <div className="mb-4">
        <div className="mb-3">
          <input
            type="text"
            className="form-control"
            placeholder="Title"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
          />
        </div>
        <div className="mb-3">
          <textarea
            className="form-control"
            placeholder="Content"
            value={content}
            onChange={(e) => setContent(e.target.value)}
            rows={4}
          />
        </div>
        <button className="btn btn-success w-100" onClick={handleAddPost}>
          Add Post
        </button>
      </div>
      <ul className="list-group">
        {posts.map(post => (
          <li key={post.id} className="list-group-item">
            <h2>{post.title}</h2>
            <p>{post.content}</p>
            <div className="d-flex justify-content-end">
              <button
                className="btn btn-danger btn-sm me-2"
                onClick={() => dispatch(removePost(post.id))}
              >
                Remove
              </button>
              <button
                className="btn btn-primary btn-sm"
                onClick={() => handleUpdatePost(post.id)}
              >
                Update
              </button>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Blog;
