// src/reducers/blogReducer.ts

import { BlogState, ADD_POST, REMOVE_POST, UPDATE_POST, BlogActionTypes } from '../blogTypes';

const initialState: BlogState = {
  posts: [],
};

export const blogReducer = (
  state = initialState,
  action: BlogActionTypes
): BlogState => {
  switch (action.type) {
    case ADD_POST:
      return {
        ...state,
        posts: [...state.posts, action.payload],
      };
    case REMOVE_POST:
      return {
        ...state,
        posts: state.posts.filter(post => post.id !== action.payload),
      };
    case UPDATE_POST:
      return {
        ...state,
        posts: state.posts.map(post =>
          post.id === action.payload.id ? { ...post, ...action.payload } : post
        ),
      };
    default:
      return state;
  }
};
