import { handleActions } from 'redux-actions';
import { HomePageActionTypes } from '../actions/homePageAction';
import { IHomePageModel } from '../actions/homePageAction';
//import { HomePageInitialState } from '../states';
 const HomePageInitialState: IHomePageModel = {
    isLoading: true,
    homePageData: [],
    homePageInfo: {
      mainSection: {},
      howItWorks: {},
      ourStory: {},
      why: {}
    },
    totalRecords: 0,
    isError: false,
  };
  
export const homePageReducer = handleActions<IHomePageModel, any>(
  {
    [HomePageActionTypes.HOMEPAGE_DETAILS_REQUEST]: (state, action) => ({
      ...state,
      isLoading: true,
    }),
    [HomePageActionTypes.HOMEPAGE_DETAILS_SUCCESS]: (state, action) => ({
      ...state,
      homePageInfo: action.payload.homePageInfo,
      isLoading: false,
    }),
    [HomePageActionTypes.HOMEPAGE_DETAILS_FAILURE]: (state, action) => ({
      ...state,
      isLoading: false,
      isError: true,
    }),
  },
  HomePageInitialState,
);
