import React, { useState } from 'react';
import './assets/css/LoginForm.scss'; // External CSS for additional styling

const defaultImage = '/assets/images.png'; // Path for public folder or use import for src folder

const LoginForm: React.FC = () => {
  const [formData, setFormData] = useState({
    fullName: '',
    email: '',
    password: '',
    confirmPassword: '',
    phoneNumber: '',
    city: '',
    address: '',
    area: '',
    gender: '',
    pincode: '',
    profileImage: ''
  });

  const [errors, setErrors] = useState({
    fullName: '',
    email: '',
    password: '',
    confirmPassword: '',
    phoneNumber: '',
    city: '',
    address: '',
    area: '',
    gender: '',
    pincode: '',
    profileImage: ''
  });

  const [message, setMessage] = useState('');

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement>) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
    setErrors({ ...errors, [name]: '' });
  };

  const handleFileChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setFormData({ ...formData, profileImage: reader.result as string });
      };
      reader.readAsDataURL(file);
    }
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();

    let newErrors = { ...errors };
    let hasError = false;

    Object.keys(formData).forEach((field) => {
      if (!formData[field as keyof typeof formData] && field !== 'profileImage') {
        newErrors = { ...newErrors, [field]: `${field.replace(/([A-Z])/g, ' $1')} is required` };
        hasError = true;
      }
    });

    if (formData.password !== formData.confirmPassword) {
      newErrors = { ...newErrors, confirmPassword: 'Passwords do not match' };
      hasError = true;
    }

    if (hasError) {
      setErrors(newErrors);
    } else {
      setMessage('Thank you for signing up!');
    }
  };

  return (
    <div className="container d-flex justify-content-center align-items-center">
      <div className="col-md-8">
        <div className="card p-5 bg-light shadow">
          <form className="login-form" onSubmit={handleSubmit}>
            <h2 className="title-bold">Signup Form</h2>

            {/* Profile Image Upload */}
            <div className="form-group text-center">
              <label htmlFor="profileImage">Profile Image</label>
              <div className="profile-image-preview">
                <img
                  src={formData.profileImage || defaultImage}
                  alt="Profile Preview"
                />
              </div>
              <input
                type="file"
                id="profileImage"
                name="profileImage"
                accept="image/*"
                onChange={handleFileChange}
              />
            </div>

            {/* Other form fields... */}

            <div className="text-center">
              <button type="submit" className="btn btn-primary btn-lg btn-block">
                Submit
              </button>
            </div>

            {message && <div className="alert alert-success mt-3 text-center">{message}</div>}
          </form>
        </div>
      </div>
    </div>
  );
};

export default LoginForm;
