import React, { useState } from 'react';
import './assets/css/CompanyForm.css'; // External CSS for additional styling
import guestAvatar from './assets/image.png'; // Path to guest image
const CompanyForm = () => {
    const [formData, setFormData] = useState({
        companyName: '',
        registrationType: '',
        regdCity: '',
        regdArea: '',
        regdPin: '',
        regdExtraField: '',
        workCity: '',
        workArea: '',
        workPin: '',
        workExtraField: '',
        directorName: '',
        gender: '',
        email: '',
        phone: '',
        loginEmail: '',
        password: '',
        confirmPassword: '',
        profileImage: '',
        gstNumber: '',
        gstCertificate: '',
        fssaiNumber: '',
        fssaiCertificate: '',
        panNumber: '',
        panCard: '',
        companyServices: '',
        agreeToTerms: false,
    });

    const [errors, setErrors] = useState<{ [key: string]: string }>({});
    const [step, setStep] = useState(1); // Track current form step

    const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement | HTMLTextAreaElement>) => {
        const { name, value, type, checked } = e.target;
        setFormData({
            ...formData,
            [name]: type === 'checkbox' ? checked : value,
        });
    };

    const validateForm = (step: number) => {
        const newErrors: { [key: string]: string } = {};
        // Validation logic based on current step
        if (step === 1) {
            if (!formData.companyName) newErrors.companyName = 'Company name is required';
            if (!formData.registrationType) newErrors.registrationType = 'Registration type is required';
        } else if (step === 2) {
            if (!formData.email) newErrors.email = 'Email is required';
            if (!/\S+@\S+\.\S+/.test(formData.email)) newErrors.email = 'Invalid email address';
            if (!formData.phone || !/^\d{10}$/.test(formData.phone)) newErrors.phone = 'Valid phone number is required';
        } else if (step === 3) {
            if (!formData.agreeToTerms) newErrors.agreeToTerms = 'You must agree to the terms and conditions';
        }
        setErrors(newErrors);
        return Object.keys(newErrors).length === 0;
    };

    const handleSubmit = (e: React.FormEvent, step: number) => {
        e.preventDefault();
        if (validateForm(step)) {
            setStep(step + 1); // Move to the next step if validation passes
        }
    };

    return (
        <div className="container mt-5">
            <h2 className="text-center">Company Information</h2>

            {/* Profile Image Section */}
            <div className="text-center mb-4">
                <img
                    src={formData.profileImage || guestAvatar}
                    alt="Profile Preview"
                    className="img-thumbnail profile-image mb-3"
                />
                <input
                    type="file"
                    id="profileImage"
                    name="profileImage"
                    accept="image/*"
                    onChange={handleChange}
                    className="form-control"
                />
            </div>

            {/* Step 1: Company & Registration Details */}
            {step === 1 && (
                <form onSubmit={(e) => handleSubmit(e, 1)} className="mb-4">
                    <h3>Company & Registration</h3>
                    <div className="form-group">
                        <label>Company Name</label>
                        <input
                            type="text"
                            name="companyName"
                            value={formData.companyName}
                            onChange={handleChange}
                            className="form-control"
                        />
                        {errors.companyName && <div className="text-danger">{errors.companyName}</div>}
                    </div>
                    <div className="form-group">
                        <label>Registration Type</label>
                        <select
                            name="registrationType"
                            value={formData.registrationType}
                            onChange={handleChange}
                            className="form-control"
                        >
                            <option value="">Select</option>
                            <option value="ltd">Ltd</option>
                            <option value="pvt ltd">Pvt Ltd</option>
                            <option value="llp">LLP</option>
                            <option value="partnership">Partnership</option>
                            <option value="firm">Firm</option>
                            <option value="upc">UPC</option>
                            <option value="individual">Individual</option>
                        </select>
                        {errors.registrationType && <div className="text-danger">{errors.registrationType}</div>}
                    </div>
                    <button type="submit" className="btn btn-primary mt-3">Next</button>
                </form>
            )}

            {/* Step 2: Contact & Login Information */}
            {step === 2 && (
                <form onSubmit={(e) => handleSubmit(e, 2)} className="mb-4">
                    <h3>Contact & Login Information</h3>
                    <div className="form-group">
                        <label>Email ID</label>
                        <input
                            type="email"
                            name="email"
                            value={formData.email}
                            onChange={handleChange}
                            className="form-control"
                        />
                        {errors.email && <div className="text-danger">{errors.email}</div>}
                    </div>
                    <div className="form-group">
                        <label>Phone Number</label>
                        <input
                            type="text"
                            name="phone"
                            value={formData.phone}
                            onChange={handleChange}
                            className="form-control"
                        />
                        {errors.phone && <div className="text-danger">{errors.phone}</div>}
                    </div>
                    <button type="submit" className="btn btn-primary mt-3">Next</button>
                </form>
            )}

            {/* Step 3: Document Uploads */}
            {step === 3 && (
                <form onSubmit={(e) => handleSubmit(e, 3)} className="mb-4">
                    <h3>Document Uploads</h3>
                    <div className="form-group">
                        <label>GST Certificate</label>
                        <input
                            type="file"
                            name="gstCertificate"
                            accept="application/pdf"
                            onChange={handleChange}
                            className="form-control"
                        />
                    </div>
                    <div className="form-group">
                        <label>FSSAI Certificate</label>
                        <input
                            type="file"
                            name="fssaiCertificate"
                            accept="application/pdf"
                            onChange={handleChange}
                            className="form-control"
                        />
                    </div>
                    <div className="form-group">
                        <label>PAN Card</label>
                        <input
                            type="file"
                            name="panCard"
                            accept="image/*"
                            onChange={handleChange}
                            className="form-control"
                        />
                    </div>

                    {/* Terms & Conditions */}
                    <div className="form-check">
                        <input
                            type="checkbox"
                            id="agreeToTerms"
                            name="agreeToTerms"
                            className={`form-check-input ${errors.agreeToTerms ? 'is-invalid' : ''}`}
                            checked={formData.agreeToTerms}
                            onChange={handleChange}
                        />
                        <label htmlFor="agreeToTerms" className="form-check-label">
                            I agree to the <a href="/terms-and-conditions" target="_blank" rel="noopener noreferrer">Terms & Conditions</a>
                        </label>
                        {errors.agreeToTerms && <div className="invalid-feedback">{errors.agreeToTerms}</div>}
                    </div>

                    <button type="submit" className="btn btn-primary mt-3">Submit</button>
                </form>
            )}
        </div>
    );
};

export default CompanyForm;
