// src/store/reducers/authReducer.ts
import {
    LOGIN_REQUEST,
    LOGIN_SUCCESS,
    LOGIN_FAILURE,
    LOGOUT,
  } from "../actions/loginActions";
  
  interface AuthState {
    isLoggedIn: boolean;
    user: any;
    error: string | null;
    attempts: number;
  }
  
  const initialState: AuthState = {
    isLoggedIn: false,
    user: null,
    error: null,
    attempts: 0,
  };
  
  export const loginReducer = (state = initialState, action: any): AuthState => {
    switch (action.type) {
      case LOGIN_REQUEST:
        return {
          ...state,
          error: null,
        };
      case LOGIN_SUCCESS:
        return {
          ...state,
          isLoggedIn: true,
          user: action.payload,
          error: null,
          attempts: 0,
        };
      case LOGIN_FAILURE:
        return {
          ...state,
          error: action.payload.error,
          attempts: action.payload.attempts,
        };
      case LOGOUT:
        return initialState;
      default:
        return state;
    }
  };
