// src/reducers/authReducer.ts

import { AuthState, LOGIN, LOGOUT, UPDATE_USER, AuthActionTypes } from '../types';

const initialState: AuthState = {
  isAuthenticated: false,
  user: null,
};

export const authReducer = (
  state = initialState,
  action: AuthActionTypes
): AuthState => {
  switch (action.type) {
    case LOGIN:
      return {
        ...state,
        isAuthenticated: true,
        user: action.payload,
      };
    case LOGOUT:
      return {
        ...state,
        isAuthenticated: false,
        user: null,
      };
    case UPDATE_USER:
      return {
        ...state,
        user: state.user ? { ...state.user, ...action.payload } : null,
      };
    default:
      return state;
  }
};
