import React, { useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { addStudent, updateStudent, deleteStudent } from '../actions/studentActions';
import 'bootstrap/dist/css/bootstrap.min.css'; // Import Bootstrap CSS

const App: React.FC = () => {
  const dispatch = useDispatch();
  const students = useSelector((state: any) => state.student.students);

  const [name, setName] = useState('');
  const [age, setAge] = useState('');
  const [selectedId, setSelectedId] = useState<number | null>(null);

  const handleAddStudent = () => {
    const newId = students.length > 0 ? students[students.length - 1].id + 1 : 1;
    dispatch(addStudent({ id: newId, name, age: parseInt(age) }));
    setName('');
    setAge('');
  };

  const handleUpdateStudent = () => {
    if (selectedId !== null) {
      dispatch(updateStudent({ id: selectedId, name, age: parseInt(age) }));
      setSelectedId(null);
      setName('');
      setAge('');
    }
  };

  const handleDeleteStudent = (id: number) => {
    dispatch(deleteStudent(id));
  };

  const handleEditStudent = (student: any) => {
    setSelectedId(student.id);
    setName(student.name);
    setAge(student.age.toString());
  };

  return (
    <div className="container mt-5">
      <h1 className="text-center mb-4">Student Management</h1>

      <div className="mb-4">
        <div className="row g-2">
          <div className="col-md-4">
            <input
              type="text"
              className="form-control"
              placeholder="Name"
              value={name}
              onChange={(e) => setName(e.target.value)}
            />
          </div>
          <div className="col-md-4">
            <input
              type="number"
              className="form-control"
              placeholder="Age"
              value={age}
              onChange={(e) => setAge(e.target.value)}
            />
          </div>
          <div className="col-md-4">
            <button
              className={`btn ${selectedId === null ? 'btn-primary' : 'btn-warning'} w-100`}
              onClick={selectedId === null ? handleAddStudent : handleUpdateStudent}
            >
              {selectedId === null ? 'Add Student' : 'Update Student'}
            </button>
          </div>
        </div>
      </div>

      <ul className="list-group">
        {students.map((student: any) => (
          <li key={student.id} className="list-group-item d-flex justify-content-between align-items-center">
            <span>
              {student.name} - {student.age}
            </span>
            <div>
              <button className="btn btn-sm btn-info me-2" onClick={() => handleEditStudent(student)}>
                Edit
              </button>
              <button className="btn btn-sm btn-danger" onClick={() => handleDeleteStudent(student.id)}>
                Delete
              </button>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default App;
