import React, { useState, useEffect } from 'react';
import './assets/css/HomePage.css';
import image1 from './assets/bp-image1.jpg';
import image2 from './assets/bp-image2.jpg';
import image3 from './assets/bp-image3.jpg';
import image4 from './assets/bp-image4.jpg';
import image5 from './assets/bp-image5.jpg';
import image6 from './assets/bp-image6.jpg';

const images = [image1, image2, image3, image4, image5, image6];

const CarouselComponent: React.FC = () => {
  const [currentIndex, setCurrentIndex] = useState(0);

  // Auto-slide effect
  useEffect(() => {
    const interval = setInterval(() => {
      goToNext();  // Automatically move to next image every 3 seconds
    }, 3000);

    return () => clearInterval(interval);  // Clear interval on component unmount
  }, [currentIndex]);

  const goToPrevious = () => {
    const newIndex = currentIndex === 0 ? images.length - 1 : currentIndex - 1;
    setCurrentIndex(newIndex);
  };

  const goToNext = () => {
    const newIndex = currentIndex === images.length - 1 ? 0 : currentIndex + 1;
    setCurrentIndex(newIndex);
  };

  return (
    <div className="carousel-container" onMouseEnter={() => clearInterval()} onMouseLeave={() => setInterval(goToNext, 3000)}>
      <button className="arrow left-arrow" onClick={goToPrevious}>
        &#9664;
      </button>
      <div className="carousel-track" style={{ transform: `translateX(-${currentIndex * 100}%)` }}>
        {images.map((image, index) => (
          <img
            key={index}
            src={image}
            alt={`carousel ${index}`}
            className="carousel-image"
          />
        ))}
      </div>
      <button className="arrow right-arrow" onClick={goToNext}>
        &#9654;
      </button>
    </div>
  );
};

export default CarouselComponent;
