import React, { useState } from 'react';
import './assets/css/LoginForm.scss'; // External CSS for additional styling
import guestAvatar from './assets/image.png'; // Assuming image path is correct

const LoginForm: React.FC = () => {
  const [formData, setFormData] = useState({
    fullName: '',
    email: '',
    password: '',
    confirmPassword: '',
    phoneNumber: '',
    city: '',
    address: '',
    area: '',
    gender: '',
    pincode: '',
    profileImage: '' // Add profileImage state
  });

  const [errors, setErrors] = useState({
    fullName: '',
    email: '',
    password: '',
    confirmPassword: '',
    phoneNumber: '',
    city: '',
    address: '',
    area: '',
    gender: '',
    pincode: '',
    profileImage: '' // Add profileImage error
  });

  const [message, setMessage] = useState('');

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement>) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
    setErrors({ ...errors, [name]: '' }); // Reset the error when the user starts typing
  };

  const handleFileChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setFormData({ ...formData, profileImage: reader.result as string }); // Set image as base64 string
      };
      reader.readAsDataURL(file);
    }
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();

    let newErrors = { ...errors };
    let hasError = false;

    // Validation for empty fields
    Object.keys(formData).forEach((field) => {
      if (!formData[field as keyof typeof formData] && field !== 'profileImage') {
        newErrors = { ...newErrors, [field]: `${field.replace(/([A-Z])/g, ' $1')} is required` };
        hasError = true;
      }
    });

    // Special check for password mismatch
    if (formData.password !== formData.confirmPassword) {
      newErrors = { ...newErrors, confirmPassword: 'Passwords do not match' };
      hasError = true;
    }

    if (hasError) {
      setErrors(newErrors);
    } else {
      setMessage('Thank you for signing up!');
    }
  };

  return (
    <div className="container d-flex justify-content-center align-items-center">
      <div className="col-md-8">
        <div className="card p-5 bg-light shadow">
          <form className="login-form" onSubmit={handleSubmit}>
            <h2 className="title-bold">Signup Form</h2> {/* Bold title */}

            {/* Profile Image Upload */}
            <div className="form-group text-center">
              <label htmlFor="profileImage">Profile Image</label>
              <div className="profile-image-preview">
                <img
                  src={formData.profileImage || guestAvatar} // Use uploaded image or fallback to guest avatar
                  alt="Profile Preview"
                  className="rounded-circle"
                />
              </div>
              <input
                type="file"
                id="profileImage"
                name="profileImage"
                accept="image/*"
                onChange={handleFileChange}
              />
            </div>

            {/* Full Name */}
            <div className="form-group">
              <label>Full Name</label>
              <input
                type="text"
                name="fullName"
                className={`form-control form-control-lg ${errors.fullName ? 'is-invalid' : ''}`}
                placeholder="Enter full name"
                value={formData.fullName}
                onChange={handleInputChange}
                required
              />
              {errors.fullName && <div className="invalid-feedback">{errors.fullName}</div>}
            </div>

            {/* Other form fields... */}

            {/* Submit Button */}
            <div className="text-center">
              <button type="submit" className="btn btn-primary btn-lg btn-block">
                Submit
              </button>
            </div>

            {/* Success Message */}
            {message && <div className="alert alert-success mt-3 text-center">{message}</div>}
          </form>
        </div>
      </div>
    </div>
  );
};

export default LoginForm;
