// src/actions/blogActions.ts

import { ADD_POST, REMOVE_POST, UPDATE_POST, BlogActionTypes, BlogPost } from '../blogTypes';

export const addPost = (post: BlogPost): BlogActionTypes => ({
  type: ADD_POST,
  payload: post,
});

export const removePost = (id: number): BlogActionTypes => ({
  type: REMOVE_POST,
  payload: id,
});

export const updatePost = (post: BlogPost): BlogActionTypes => ({
  type: UPDATE_POST,
  payload: post,
});
