// src/LanguageSelector.js
import React, { useState } from 'react';
import './assets/css/LanguageSelector.css'; // Import CSS for styling

const languages = [
    'Hindi',
    'Bengali',
    'Telugu',
    'Marathi',
    'Tamil',
    'Gujarati',
    'Urdu',
    'Malayalam',
    'Kannada',
    'Odia',
    'Punjabi',
    'Assamese',
    'Maithili',
    'Dogri',
    'Santhali',
    'Kashmiri',
    // Add more languages as needed
];

const LanguageSelector = () => {
    const [isOpen, setIsOpen] = useState(false);
    const [selectedLanguage, setSelectedLanguage] = useState('');

    const handleImageClick = () => {
        setIsOpen(!isOpen); // Toggle the language list visibility
    };

    const handleLanguageClick = (language) => {
        setSelectedLanguage(language);
        setIsOpen(false); // Close the language list
        alert(`You selected: ${language}`);
    };

    return (
        <div className="language-selector">
            <img
                src="/assets/Language-image.jpg" // Replace with your image path
                alt="Select Language"
                className="selector-image"
                onClick={handleImageClick}
            />
            {isOpen && (
                <ul className="language-list">
                    {languages.map((lang) => (
                        <li key={lang} onClick={() => handleLanguageClick(lang)}>
                            {lang}
                        </li>
                    ))}
                </ul>
            )}
            {selectedLanguage && <h2>You have selected: {selectedLanguage}</h2>}
        </div>
    );
};

export default LanguageSelector;
