import React, { useState } from 'react';
import './assets/css/Home.css'; // Ensure this file contains necessary CSS
import image1 from './assets/bp-image1.jpg';
import image2 from './assets/bp-image2.jpg';
import image3 from './assets/bp-image3.jpg';
import image4 from './assets/bp-image4.jpg';
import image5 from './assets/bp-image5.jpg';
import image6 from './assets/bp-image6.jpg';

const images = [image1, image2, image3, image4, image5, image6];


const CarouselComponent: React.FC = () => {
  const [currentIndex, setCurrentIndex] = useState(0);

  const goToNext = () => {
    setCurrentIndex((prevIndex) => (prevIndex + 1) % images.length);
  };

  const goToPrevious = () => {
    setCurrentIndex((prevIndex) => (prevIndex - 1 + images.length) % images.length);
  };

  const goToSlide = (index: number) => {
    setCurrentIndex(index);
  };

  return (
    <div id="demo" className="carousel slide" data-bs-ride="carousel">
      {/* Indicators */}
      <div className="carousel-indicators">
        {images.map((_, index) => (
          <button
            key={index}
            type="button"
            data-bs-target="#demo"
            data-bs-slide-to={index}
            className={currentIndex === index ? 'active' : ''}
            onClick={() => goToSlide(index)}
            aria-current={currentIndex === index ? 'true' : undefined}
          />
        ))}
      </div>

      {/* Carousel items */}
      <div className="carousel-inner">
        {images.map((image, index) => (
          <div key={index} className={`carousel-item ${currentIndex === index ? 'active' : ''}`}>
            <img src={image.src} alt={image.alt} className="d-block" />
          </div>
        ))}
      </div>

      {/* Left and right controls */}
      <button className="carousel-control-prev" type="button" onClick={goToPrevious}>
        <span className="carousel-control-prev-icon" aria-hidden="true"></span>
      </button>
      <button className="carousel-control-next" type="button" onClick={goToNext}>
        <span className="carousel-control-next-icon" aria-hidden="true"></span>
      </button>
    </div>
  );
};

export default CarouselComponent;
