import React, { useState, useEffect } from 'react';
import './assets/css/HomePage.css';
import image1 from './assets/bp-image1.jpg';
import image2 from './assets/bp-image2.jpg';
import image3 from './assets/bp-image3.jpg';
import image4 from './assets/bp-image4.jpg';
import image5 from './assets/bp-image5.jpg';
import image6 from './assets/bp-image6.jpg';

const images = [image1, image2, image3, image4, image5, image6];

const CarouselComponent = () => {
  const [currentIndex, setCurrentIndex] = useState(0);

  // Slide to next image every 3 seconds
  useEffect(() => {
    const slideInterval = setInterval(() => {
      goToNext();
    }, 3000); // 3 seconds interval for automatic slide

    // Cleanup the interval on unmount
    return () => clearInterval(slideInterval);
  }, [currentIndex]);

  // Handle previous slide
  const goToPrevious = () => {
    const isFirstSlide = currentIndex === 0;
    const newIndex = isFirstSlide ? images.length - 1 : currentIndex - 1;
    setCurrentIndex(newIndex);
  };

  // Handle next slide
  const goToNext = () => {
    const isLastSlide = currentIndex === images.length - 1;
    const newIndex = isLastSlide ? 0 : currentIndex + 1;
    setCurrentIndex(newIndex);
  };

  return (
    <div className="carousel-container">
      <div className="carousel-slide">
        {/* Left Arrow */}
        <button className="left-arrow" onClick={goToPrevious}>
          &#10094;
        </button>
        
        {/* Image */}
        <img src={images[currentIndex]} alt={`carousel ${currentIndex}`} className="carousel-image" />

        {/* Right Arrow */}
        <button className="right-arrow" onClick={goToNext}>
          &#10095;
        </button>
      </div>
    </div>
  );
};

export default CarouselComponent;
