// src/actions/studentActions.ts

import { ADD_STUDENT, UPDATE_STUDENT, DELETE_STUDENT } from '../types';

interface Student {
  id: number;
  name: string;
  age: number;
}

export const addStudent = (student: Student) => ({
  type: ADD_STUDENT,
  payload: student,
});

export const updateStudent = (student: Student) => ({
  type: UPDATE_STUDENT,
  payload: student,
});

export const deleteStudent = (id: number) => ({
  type: DELETE_STUDENT,
  payload: id,
});
